import React, {Component} from 'react'
import { Link } from "react-router-dom";

class Form extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fileInput = React.createRef();  
        this.csvToArray = this.csvToArray.bind(this);
        this.samplingArray = this.samplingArray.bind(this); 
    }

    handleSubmit(event) {
        event.preventDefault();
        const input = this.fileInput.current.files[0];
        const reader = new FileReader();

        reader.onload = function (e) {
            const text = e.target.result;
            const data = this.csvToArray(text);
            console.log(data);
            console.log(data[0]["common_name"]);
            this.samplingArray(data);
        };

        reader.readAsText(input);

        alert(
            `Selected file - ${this.fileInput.current.files[0].name}`    );
    }

    csvToArray(str, delimiter = ",") {
        const headers = str.slice(0, str.indexOf("\n")).split(delimiter);
        const rows = str.slice(str.indexOf("\n") + 1).split("\n");

        const arr = rows.map(function (row) {
          const values = row.split(delimiter);
          const el = headers.reduce(function (object, header, index) {
            object[header] = values[index];
            return object;
          }, {});
          return el;
        });

        return arr;
      }

    samplingArray(data){
        console.log(data.length);
    }

    render() {
        return (
            <div className="box">
                <div className="form">
                    <div className="title">Carica</div>
                    <div className="contenuto">
                        <form onSubmit={this.handleSubmit} id="myForm">
                            <div className="bottone">
                                <input type="file" accept=".csv" id="csvFile" ref={this.fileInput}/>
                            </div>
                            <div className=" elaborazione_form">
                                <button type="submit"className='button'>Submit</button>
                            {/*<Link to="/result" className="button">Elabora</Link>*/}
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Form;
