import * as d3 from 'd3';
import { create } from 'd3';
import { dataD3, xAxis, yAxis } from './control.js';

//create a div element for D3 to build upon
var node = document.createElement('div');

const margin = {top: 10, right: 30, bottom: 30, left: 60},
    width = 460 - margin.left - margin.right,
    height = 400 - margin.top - margin.bottom;

// append the svg object to the body of the page
const svg = d3.select(node)
  .append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
  .append("g")
    .attr("transform",
          "translate(" + margin.left + "," + margin.top + ")");


export function createScatter(){
  // Add X axis
  const x = d3.scaleLinear()
  .domain([min(dataD3, xAxis), max(dataD3, xAxis)])
  .range([ 0, width ]);

  svg.append("g")
  .attr("transform", `translate(0, ${height})`)
  .call(d3.axisBottom(x));

  // Add Y axis
  const y = d3.scaleLinear()
  .domain([min(dataD3, yAxis), max(dataD3, yAxis)])
  .range([ height, 0]);

  svg.append("g")
  .call(d3.axisLeft(y));

  // Add dots
  svg.append('g')
  .selectAll("dot")
  .dataD3(dataD3)
  .join("circle")
  .attr("cx", function (d) { return x(d[xAxis]); } )
  .attr("cy", function (d) { return y(d[yAxis]); } )
  .attr("r", 3.5)
  .style("fill", "#69b3a2");

}

function min(dataD3, ybj){
  let min = dataD3[0][xAxis];
  dataD3.forEach((item, i) => {
    if(item[xAxis] < min){
      min = item[xAxis];
    }
  });
  console.log(min);
  return min;
}

function max(dataD3, xAxis){
  let max = dataD3[0][xAxis];
  dataD3.forEach((item, i) => {
    if(item[xAxis] > max){
      max = item[xAxis];
    }
  });
  console.log(max);
  return max;
}



export default node;
