import React, {Component} from 'react'
import { Link } from "react-router-dom";

class Form extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fileInput = React.createRef();  
    }

    handleSubmit(event) {
        event.preventDefault();
        const input = this.fileInput.files[0];
        const reader = new FileReader();

        reader.onload = function (e) {
            const text = e.target.result;
            const data = csvToArray(text);
            console.log(data);
            console.log(data[0]["common_name"]);
            samplingArray(data);
        };

        reader.readAsText(input);
    }

    render() {
        return (
            <div className="box">
                <div className="form">
                    <div className="title">Carica</div>
                    <div className="contenuto">
                        <form onSubmit={this.handleSubmit} id="myForm">
                            <div className="bottone">
                                <input type="file" accept=".csv" id="csvFile" ref={this.fileInput}/>
                            </div>
                            <div className=" elaborazione_form">
                                <button type="submit"className='button'>Submit</button>
                            {/*<Link to="/result" className="button">Elabora</Link>*/}
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Form;
